css:
/* Layout */
atom-panel.modal.expose-panel {
  left: 0;
  margin: auto;
  width: 100%;
  height: 100%;
  overflow: scroll;
  background-color: rgba(0, 0, 0, 0.4);
}
atom-workspace-axis.expose-blur {
  -webkit-filter: blur(5px);
}
.expose-view {
  height: 100%;
  /* Tabs numbers */
  /* Modal zoom animation */
  /* Helpers */
}
.expose-view .expose-top {
  text-align: right;
}
.expose-view .expose-top a::before {
  font-size: 32px;
  height: 32px;
  width: 32px;
}
.expose-view .tab-list {
  display: -webkit-flex;
  justify-content: space-around;
  flex-flow: row wrap;
}
.expose-view .tab-list .expose-tab {
  width: 210px;
  margin: 10px 5px;
  -webkit-user-drag: element;
  border: #333 solid 1px;
  background: #282c34;
}
.expose-view .tab-list .expose-tab .tab-header {
  height: 32px;
  padding: 8px 2px;
  text-align: center;
  cursor: move;
}
.expose-view .tab-list .expose-tab .tab-header .title {
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  margin: 0 15px;
}
.expose-view .tab-list .expose-tab .tab-header .title::before {
  margin-right: 5px;
}
.expose-view .tab-list .expose-tab .tab-header .close-icon {
  float: right;
  margin-top: -17px;
  cursor: pointer;
  transform: scale(0);
  transition-duration: .08s;
}
.expose-view .tab-list .expose-tab .tab-body {
  position: relative;
  display: flex;
  justify-content: center;
  align-items: center;
  overflow: hidden;
  height: 140px;
  padding: 5px;
  border-top-style: solid;
  border-width: 1px;
  cursor: pointer;
}
.expose-view .tab-list .expose-tab .tab-body img {
  max-height: 100%;
}
.expose-view .tab-list .expose-tab .tab-body a::before {
  font-size: 64px;
  height: 64px;
  width: 64px;
}
.expose-view .tab-list .expose-tab .tab-body a.animate::before {
  -webkit-animation: spin 1s infinite linear;
}
.expose-view .tab-list .expose-tab:hover .tab-body {
  box-shadow: 0 1px 5px 0 rgba(0, 0, 0, 0.5);
}
.expose-view .tab-list .expose-tab:hover .close-icon {
  transform: scale(1);
  transition-duration: .12s;
}
.expose-view .tab-list .expose-tab.active .tab-body {
  border-style: solid dashed dashed;
  padding-bottom: 4px;
}
.expose-view .tab-list .expose-tab.active .tab-body atom-text-editor-minimap {
  margin-left: -1px;
}
.expose-view .expose-tab:not(:hover):nth-of-type(1) .close-icon {
  transform: none;
}
.expose-view .expose-tab:not(:hover):nth-of-type(1) .close-icon::before {
  font-family: Helvetica, Arial, sans-serif;
  content: "1";
  font-size: 12px;
  font-weight: bold;
}
