}
.expose-view.animate {
  transform: scale(0.7);
  opacity: 0;
  transition: all 0.1s;
}
.expose-view.animate.visible {
  transform: scale(1);
  opacity: 1;
}
.expose-view .ghost {
  opacity: 0.4;
}
@-webkit-keyframes spin {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
}
